mapper.delete:
DELETE FROM sky_take_out.setmeal
WHERE id IN

    #{id}

mapper.select:
SELECT s.id, s.category_id AS category_id, s.name, s.price, s.status, s.image, s.update_time, c.name AS category_name
FROM sky_take_out.setmeal s
LEFT JOIN sky_take_out.category c ON s.category_id = c.id


        s.category_id = #{categoryId}


        AND s.name LIKE CONCAT('%', #{name}, '%')


        AND s.status = #{status}
